import { names } from './js/names';
//const names = require('./js/names');
console.log('names=>', names);

//14.110 * 30 / 1000 = 4233
const namesLength = (names.length * 30) / 100;
const selectedNames = [];
let speakNames = [];
window.SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();

let transcript = '';

let lastName = '';
let pcLastName = '';
let pcName = '';
let tmpPcName = '';
for (let index = 0; index < namesLength; index++) {
  const random = Math.floor(Math.random() * namesLength);
  selectedNames.push(names[random]);
}

console.log('selectedNames=>', selectedNames);

recognition.onstart = function () {
  console.log('start mic');
};
recognition.onresult = function (event) {
  recognition.lang = 'tr-TR';
  transcript = '';
  transcript = Array.from(event.results)
    .map((result) => result[0])
    .map((result) => result.transcript)
    .join('');
  if (pcLastName != transcript.toLowerCase().substring(0, 1))
    console.log('--ELENDIN--');
  else {
    lastName = transcript.slice(-1);

    namesControll(transcript.toLowerCase());
  }

  setTimeout(() => {
    if (transcript === '') console.log('ELENDIN 5 SANIYE KONUSMADIN');
    else speaking();
  }, 5000);
};
function speakGamer() {
  console.log('oyun gamer');

  recognition.start();

  setTimeout(() => {
    if (transcript === '') console.log('ELENDIN 5 SANIYE KONUSMADIN');
  }, 5000);
}

function speaking() {
  console.log('speaking PC');

  if (lastName === '') {
    const randomSelectedName = Math.floor(Math.random() * selectedNames.length);
    tmpPcName = selectedNames[randomSelectedName];
  } else {
    pcName = selectedNames.filter((n) => n.substring(0, 1) === lastName);
    const random = Math.floor(Math.random() * pcName.length);

    tmpPcName = pcName[random];
  }

  namesControll(tmpPcName.toLowerCase());
  pcLastName = tmpPcName.toLowerCase().slice(-1);
  const synth = window.speechSynthesis;
  let utterThis = new SpeechSynthesisUtterance('mert');
  synth.speak(utterThis);
  speakGamer();
}

function namesControll(item) {
  console.log('item=>', item, 'speakNames', speakNames);
  if (speakNames.indexOf(item) === -1) speakNames.push(item);
  else console.log('ELENDIN');
}
speaking();
